import React, { useState, useEffect } from 'react';
import './ImageSlider.css';

const ImageSlider = ({ images }) => {
  const [current, setCurrent] = useState(0);

  useEffect(() => {
    const timer = setTimeout(() => {
      handleNext();
    }, 2000); // 2 seconds

    return () => {
      clearTimeout(timer);
    };
  }, [current]);

  const handlePrev = () => {
    setCurrent((prevCurrent) => (prevCurrent === 0 ? images.length - 1 : prevCurrent - 1));
  };

  const handleNext = () => {
    setCurrent((prevCurrent) => (prevCurrent === images.length - 1 ? 0 : prevCurrent + 1));
  };

  return (
    <div className="image-slider">
      <button className="slider-button slider-button-prev" onClick={handlePrev}>
        &lsaquo;
      </button>
      <div className="slider-images">
        {images.map((image, index) => (
          <img
            key={index}
            src={image}
            alt={`Slide ${index + 1}`}
            className={`slider-image${index === current ? ' active' : ''}`}
          />
        ))}
      </div>
      <button className="slider-button slider-button-next" onClick={handleNext}>
        &rsaquo;
      </button>
    </div>
  );
};

export default ImageSlider;
