import React, { useEffect, useState } from 'react';
import './Tutorial.css';

function Tutorial() {
  const [iframeStyle, setIframeStyle] = useState({});

  useEffect(() => {
    const footerHeight = document.querySelector('.footer').offsetHeight;
    const tutorialHeight = footerHeight * 2; // height is 1.25 times footer height
    const tutorialWidth = tutorialHeight * (16 / 9); // maintain 16:9 aspect ratio

    setIframeStyle({
      width: `${tutorialWidth}px`,
      height: `${tutorialHeight}px`,
    });
  }, []);

  return (
    <div className="tutorial">
      <h2 className="tutorial__title">Informaci√≥n</h2>
      <p className="tutorial__text">
        Here is some text about how to take the images. 
        <img className="tutorial__image" src="path-to-your-image" alt="Description" style={iframeStyle} />
      </p>
      <iframe 
        className="tutorial__video"
        src="https://www.youtube.com/embed/k4NTf0hMjtY?start=114" 
        title="YouTube video player" 
        style={iframeStyle}
        allowFullScreen>
      </iframe>
    </div>
  );
}

export default Tutorial;
