import React, { useEffect, useState } from 'react';
import './Tutorial.css';

function Tutorial() {
  const [height, setHeight] = useState('56.25%');  // default aspect ratio for 16:9

  useEffect(() => {
    const footerHeight = document.querySelector('.footer').offsetHeight;
    const videoHeight = footerHeight * 1.25;
    setHeight(`${videoHeight}px`);
  }, []);

  const tutorialStyle = {
    paddingTop: height,
    // Other styles...
  };

  return (
    <div className="tutorial" style={tutorialStyle}>
      <iframe 
        className="tutorial__video"
        src="https://www.youtube.com/embed/k4NTf0hMjtY?start=114" 
        title="YouTube video player" 
        allowFullScreen>
      </iframe>
    </div>
  );
}

export default Tutorial;
